sp_angular_provider.script:
function (){
		return{
			template:  ' <iframe class="youtube-player" type="text/html" width="90%" height="406,78px" ng-src="{{trustSrc(videoclass.link)}}" allowfullscreen frameborder="0">',
			restrict: 'E',
			scope: {
			'videoclass': '='
			},
			controller: function($scope,$sce){
			 $scope.trustSrc = function(src) {
    return $sce.trustAsResourceUrl(src);
  }

			}

		};
	}
